mapper.select:
SELECT u.* FROM sys_user u
WHERE u.deleted = 0

    AND (u.username LIKE CONCAT('%', #{keyword}, '%') 
         OR u.real_name LIKE CONCAT('%', #{keyword}, '%')
         OR u.phone LIKE CONCAT('%', #{keyword}, '%'))


    AND u.status = #{status}


    AND u.role_id = #{roleId}


    AND u.institution_id = #{institutionId}

ORDER BY u.create_time DESC

mapper.insert:
INSERT INTO sys_user (
    username, password, real_name, phone, email, id_card, avatar,
    gender, user_type, institution_id, institution_name, department,
    position, title, role_id, role_name, status, audit_status, create_time, create_by
) VALUES (
    #{username}, #{password}, #{realName}, #{phone}, #{email}, #{idCard}, #{avatar},
    #{gender}, #{userType}, #{institutionId}, #{institutionName}, #{department},
    #{position}, #{title}, #{roleId}, #{roleName}, #{status}, #{auditStatus}, NOW(), #{createBy}
)

mapper.update:
UPDATE sys_user SET deleted = 1, update_time = NOW()
WHERE id IN

    #{id}
